import React, { Component } from "react";
import { Form, Button, Input, Message, Grid } from "semantic-ui-react";
import Layout from "../components/Layout";
import web3 from "../ethereum/web3";
import { Router } from "../routes";

class Wallet extends Component {
  state = {
    tokenAddress: ""
  };

  onSubmit = event => {
    if (this.state.tokenAddress != "") {
      var mintRoute = "/t/" + this.state.tokenAddress;
      Router.pushRoute(mintRoute);
    }
  };

  render() {
    return (
      <Layout page="mint">
        <Grid>
          <Grid.Row>
            <Grid.Column>
              <h2>InvestMint Token Mint</h2>
            </Grid.Column>
          </Grid.Row>
          <Grid.Row>
            <Grid.Column width={10}>
              <center>
                <Form onSubmit={this.onSubmit}>
                  <Form.Field>
                    <Input
                      placeholder="InvestMint Token Address: 0x..."
                      value={this.state.tokenAddress}
                      onChange={event =>
                        this.setState({ tokenAddress: event.target.value })
                      }
                    />
                  </Form.Field>
                  <Button
                    size="huge"
                    color="teal"
                    loading={this.state.sellTokensLoading}
                  >
                    Launch Mint
                  </Button>
                </Form>
              </center>
            </Grid.Column>
          </Grid.Row>
        </Grid>
      </Layout>
    );
  }
}

export default Wallet;
